mapper.select:
SELECT sg.id, sg.gcId,sg.gcName, sg.goodsImageMore, sg.goodsName, sg.brandId, sg.brandName, sg.goodsShow, sz.specGoodsStorage,sg.goodsClick, sg.saleNum, sz.specGoodsPrice,sg.goodsBodyApp,sg.goodsBodyPc, sg.goodsIsComplete, sg.isAudit, sg.goodsStaus, sz.specStorageWarning, sz.id as specId, sz.specName,sz.specNameValue, sg.goodsCommend,sg.goodsSpec,sz.integralCount,sg.goodsVideo, sg.created,sg.merchantId FROM shop_goods sg LEFT JOIN (SELECT id,goodsId,specName,specNameValue,specGoodsPrice,specGoodsStorage,specGoodsSerial,specIsOpen,integralCount,specStorageWarning FROM shop_goods_spec a, (SELECT MIN(specGoodsPrice) AS price,goodsId as groupId FROM shop_goods_spec AS sgs WHERE sgs.specIsOpen = 1 GROUP BY goodsId) b where a.goodsId = b.groupId and specGoodsPrice = price
and a.specGoodsStorage <= a.specStorageWarning
GROUP BY goodsId) sz ON sg.id = sz.goodsId WHERE 1=1
and sg.goodsShow = #{goodsShow}
AND sg.goodsName LIKE #{goodsName}
AND sg.gcId = #{gcId}
AND sg.brandId = #{brandId}
and sg.id = #{goodsId}
and sg.goodsCommend = ${goodsCommend} ORDER BY sg.created DESC
ORDER BY sg.goodsClick DESC, sg.created DESC

SELECT sg.id, sg.gcId,sg.gcName, sg.goodsImageMore, sg.goodsName, sg.brandId, sg.brandName,
             sg.goodsShow, sz.specGoodsStorage,sg.goodsClick, sg.saleNum, sz.specGoodsPrice,
       sg.goodsIsComplete, sg.isAudit, sg.goodsStaus, sz.specStorageWarning,sg.specialType,
       sz.id as specId, sz.specName,sz.specNameValue, sg.goodsCommend,sg.goodsSpec,sz.integralCount,sg.goodsVideo,
       sg.created
FROM shop_goods sg
LEFT JOIN
(SELECT id,goodsId,specName,specNameValue,specGoodsPrice,specGoodsStorage,specGoodsSerial,specIsOpen,integralCount,specStorageWarning
FROM shop_goods_spec a,
(SELECT MIN(specGoodsPrice) AS price,goodsId as groupId FROM shop_goods_spec AS sgs WHERE sgs.specIsOpen = 1 GROUP BY goodsId) b
where a.goodsId = b.groupId and specGoodsPrice = price

    and a.specGoodsStorage  <=  a.specStorageWarning

GROUP BY goodsId) sz
ON sg.id = sz.goodsId
WHERE 1=1

    and sg.goodsShow = #{goodsShow}


    AND sg.goodsName LIKE #{goodsName}


    AND sg.gcId = #{gcId}


    AND sg.brandId = #{brandId}


    and sg.id = #{goodsId}


    and sg.goodsCommend = ${goodsCommend}
    ORDER BY sg.created DESC


    and sg.specialType = ${specialType}


    and sg.specialType in (1,3,4)


    and sg.isAudit = ${isAudit}


    ORDER BY sg.goodsClick DESC, sg.created DESC
